query.sql:
DECLARE
  cnt number :=1;
TYPE myrecord
IS
  RECORD
  (
    sgmnt     VARCHAR2(128) := '',
    sgmnttype VARCHAR2(128) := '',
    block_id  NUMBER        := 0,
    blks     NUMBER        := 0,
    sgmntlag  varchar2(128) := '',
    sgmntlead varchar2(128) := '');
TYPE mytable
IS
  TABLE OF myrecord INDEX BY BINARY_INTEGER;
  mydt mytable;
  myt  myrecord;
  CURSOR mycur
  IS
  WITH a AS
    (SELECT owner
      ||'.'
      ||segment_name sgmnt,
      segment_type sgmnttype,
      block_id,
      blocks blks,
      block_id-lag(block_id+blocks,1,0) over (order by block_id ASC) diff,
      lag(owner
      ||'.'
      ||segment_name,1,NULL) over (order by block_id ASC) sgmntlag,
      lag(blocks,1,0) over (order by block_id ASC) lagblocks,
      lag(block_id,1,0) over (order by block_id ASC) lagblock_id,
      lead(owner
      ||'.'
      ||segment_name,1,NULL) over (order by block_id ASC) sgmntlead
    FROM dba_extents
    WHERE file_id=:FILE_ID
    ORDER BY block_id ASC
    )
SELECT sgmnt,
  sgmnttype,
  block_id,
  blks,
  sgmntlag,
  sgmntlead
FROM
  ( SELECT sgmnt,sgmnttype,block_id,blks,sgmntlag,sgmntlead FROM a
  UNION ALL
  SELECT 'FREE',
    'FREE',
    block_id-diff,
    diff    -1,
    null,
    null
  FROM a
  WHERE diff<>0
  ORDER BY 3 ASC
  );
BEGIN
  --if mycur %ISOPEN THEN  close mycur; end if;
  --OPEN mycur;
  --
  DBMS_SESSION.FREE_UNUSED_USER_MEMORY;
  DBMS_OUTPUT.ENABLE (buffer_size => 9600000);
  dbms_output.put_line('<PRE>');
    dbms_output.put_line(
    rpad('Segment type',32)||
    rpad('Segment',96)||
    rpad('Block Id',15)||
    rpad('Blocks',15));
    --dbms_output.new_line;
  for dt in mycur loop
  if dt.sgmntlag is null or (dt.sgmntlag<>dt.sgmnt) then
    myt.sgmnt := dt.sgmnt; myt.sgmnttype := dt.sgmnttype; myt.block_id := dt.block_id; myt.blks := dt.blks;
  else
    myt.blks := myt.blks + dt.blks;
  end if;
  if dt.sgmntlead is null or dt.sgmnt<>dt.sgmntlead then
    mydt(cnt) := myt;
    dbms_output.put_line(
    rpad(mydt(cnt).sgmnttype,32)||
    rpad(mydt(cnt).sgmnt,96)||
    rpad(mydt(cnt).block_id,15)||
    rpad(mydt(cnt).blks,15));
    --dbms_output.new_line;
    cnt := cnt+1;
  end if;
  end loop;
  dbms_output.put_line('</PRE>');
  DBMS_SESSION.FREE_UNUSED_USER_MEMORY;
  --
  --CLOSE mycur;
END;
