item.sql:
BEGIN
  FOR a IN
  (SELECT sid,
    serial#,
    inst_id
  FROM gv$session
  WHERE username=:USERNAME
  AND username <>'SYS'
  )
  LOOP
    BEGIN
    EXECUTE immediate 'alter system disconnect session '''||a.sid||','||a.serial#||',@'||a.inst_id||''' immediate';
	EXCEPTION WHEN OTHERS THEN  NULL;
	END;
  END LOOP;
END;

